import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Form, FormControl, FormField, FormItem, FormMessage } from "@/components/ui/form";
import { Mail, X } from "lucide-react";
import { useToast } from "@/hooks/use-toast";

const verificationSchema = z.object({
  code: z.string().length(6, "Verification code must be 6 digits").regex(/^\d+$/, "Code must contain only numbers"),
});

type VerificationFormData = z.infer<typeof verificationSchema>;

interface EmailVerificationModalProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  email?: string;
  onVerificationComplete?: () => void;
}

export default function EmailVerificationModal({ 
  open, 
  onOpenChange, 
  email = "john@university.edu",
  onVerificationComplete 
}: EmailVerificationModalProps) {
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [isResending, setIsResending] = useState(false);
  const [showCodeInput, setShowCodeInput] = useState(false);
  const { toast } = useToast();

  const form = useForm<VerificationFormData>({
    resolver: zodResolver(verificationSchema),
    defaultValues: {
      code: "",
    },
  });

  const onSubmit = async (data: VerificationFormData) => {
    setIsSubmitting(true);
    try {
      // Here you would typically make an API call to verify the code
      console.log("Verifying code:", data.code);

      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 1000));

      toast({
        title: "Email Verified!",
        description: "Your account has been successfully verified.",
      });

      // Call completion callback and close modal
      onVerificationComplete?.();
      onOpenChange(false);
      form.reset();
    } catch (error) {
      console.error("Error verifying code:", error);
      toast({
        variant: "destructive",
        title: "Verification Failed",
        description: "Invalid verification code. Please try again.",
      });
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleResendEmail = async () => {
    setIsResending(true);
    try {
      // Here you would typically make an API call to resend the verification email
      console.log("Resending verification email to:", email);

      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 1000));

      toast({
        title: "Email Sent!",
        description: "We've sent another verification link to your email.",
      });
    } catch (error) {
      console.error("Error resending email:", error);
      toast({
        variant: "destructive",
        title: "Error",
        description: "Failed to resend verification email. Please try again.",
      });
    } finally {
      setIsResending(false);
    }
  };

  const handleOpenEmailApp = () => {
    // Try to open the default email client
    window.location.href = "mailto:";
  };

  // Split the verification code into individual digits for display
  const codeValue = form.watch("code");
  const codeDigits = Array.from({ length: 6 }, (_, i) => codeValue[i] || "");

  const handleCodeChange = (value: string, index: number) => {
    const newCode = codeDigits.slice();
    newCode[index] = value.slice(-1); // Only take the last character
    const newCodeString = newCode.join("");
    form.setValue("code", newCodeString);

    // Auto-focus next input if current one is filled
    if (value && index < 5) {
      const nextInput = document.querySelector(`[data-testid="code-input-${index + 1}"]`) as HTMLInputElement;
      nextInput?.focus();
    }
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="sm:max-w-md">
        <DialogHeader className="flex flex-row items-center justify-between">
          <DialogTitle className="text-xl font-semibold text-center flex-1" data-testid="heading-verify-email">
            Verify Your Email
          </DialogTitle>
          <Button
            variant="ghost"
            size="sm"
            className="h-6 w-6 p-0"
            onClick={() => onOpenChange(false)}
            data-testid="button-close-verification-modal"
          >
            <X className="h-4 w-4" />
          </Button>
        </DialogHeader>

        <div className="space-y-6 py-4">
          {/* Email Icon */}
          <div className="flex justify-center">
            <div className="w-16 h-16 bg-primary/10 rounded-full flex items-center justify-center">
              <Mail className="h-8 w-8 text-primary" />
            </div>
          </div>

          {/* Email Sent Message */}
          <div className="text-center space-y-2">
            <p className="text-foreground" data-testid="text-email-sent">
              We sent a verification link to
            </p>
            <p className="font-medium text-foreground" data-testid="text-email-address">
              {email}
            </p>
            <p className="text-muted-foreground text-sm" data-testid="text-verification-instructions">
              Click the link to verify your account and get started
            </p>
          </div>

          {/* Action Buttons */}
          <div className="flex gap-3">
            <Button 
              variant="outline" 
              className="flex-1"
              onClick={handleOpenEmailApp}
              data-testid="button-open-email-app"
            >
              Open Email App
            </Button>
            <Button 
              variant="outline" 
              className="flex-1"
              onClick={handleResendEmail}
              disabled={isResending}
              data-testid="button-resend-email"
            >
              {isResending ? "Sending..." : "Resend"}
            </Button>
          </div>

          {/* Spam Message */}
          <div className="text-center text-sm text-muted-foreground">
            <span>Didn't receive? Check spam or </span>
            <Button
              variant="link"
              className="p-0 h-auto text-sm"
              onClick={handleResendEmail}
              disabled={isResending}
              data-testid="button-try-again"
            >
              try again
            </Button>
          </div>

          {/* Manual Code Entry */}
          <div className="text-center">
            <Button
              variant="link"
              className="text-sm"
              onClick={() => setShowCodeInput(!showCodeInput)}
              data-testid="button-enter-code-manually"
            >
              Enter code manually
            </Button>
          </div>

          {/* Code Input Section */}
          {showCodeInput && (
            <Form {...form}>
              <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                <FormField
                  control={form.control}
                  name="code"
                  render={() => (
                    <FormItem>
                      <FormControl>
                        <div className="flex justify-center gap-2">
                          {Array.from({ length: 6 }, (_, index) => (
                            <Input
                              key={index}
                              type="text"
                              inputMode="numeric"
                              maxLength={1}
                              className="w-12 h-12 text-center text-lg font-medium"
                              value={codeDigits[index]}
                              onChange={(e) => handleCodeChange(e.target.value, index)}
                              onKeyDown={(e) => {
                                // Handle backspace
                                if (e.key === "Backspace" && !codeDigits[index] && index > 0) {
                                  const prevInput = document.querySelector(`[data-testid="code-input-${index - 1}"]`) as HTMLInputElement;
                                  prevInput?.focus();
                                }
                              }}
                              data-testid={`code-input-${index}`}
                            />
                          ))}
                        </div>
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <Button
                  type="submit"
                  className="w-full"
                  disabled={isSubmitting || codeValue.length !== 6}
                  data-testid="button-verify-continue"
                >
                  {isSubmitting ? "Verifying..." : "Verify & Continue"}
                </Button>
              </form>
            </Form>
          )}
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/auth/login-modal.t.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Form } from "@/components/ui/form";
import { FormTextField, FormCheckboxField } from "@/components/form";
import { X } from "lucide-react";
import { useAuth } from "@/context/AuthContext";

const loginSchema = z.object({
  email: z.string().email("Please enter a valid email address"),
  password: z.string().min(1, "Password is required"),
  rememberMe: z.boolean(),
});

type LoginFormData = z.infer<typeof loginSchema>;

interface LoginModalProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  onSignupClick?: () => void;
}

export default function LoginModal({ open, onOpenChange, onSignupClick }: LoginModalProps) {
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [authError, setAuthError] = useState<string | null>(null);
  const { signIn, signInWithOAuth } = useAuth();

  const form = useForm<LoginFormData>({
    resolver: zodResolver(loginSchema),
    defaultValues: {
      email: "",
      password: "",
      rememberMe: false,
    },
  });

  const onSubmit = async (data: LoginFormData) => {
    setIsSubmitting(true);
    setAuthError(null);

    try {
      const { error } = await signIn(data.email, data.password);

      if (error) {
        setAuthError(error.message || "Failed to sign in. Please check your credentials.");
        return;
      }

      // Close modal on success
      onOpenChange(false);
      form.reset();
    } catch (error) {
      setAuthError("An unexpected error occurred. Please try again.");
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleSocialLogin = async (provider: 'google' | 'facebook') => {
    setAuthError(null);
    try {
      const { error } = await signInWithOAuth(provider);
      if (error) {
        setAuthError(error.message || `Failed to sign in with ${provider}`);
      }
    } catch (error) {
      setAuthError(`An error occurred signing in with ${provider}`);
    }
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="sm:max-w-md">
        <DialogHeader className="flex flex-row items-center justify-between">
          <DialogTitle className="text-xl font-semibold text-center flex-1" data-testid="heading-login">
            Welcome Back
          </DialogTitle>
          <Button
            variant="ghost"
            size="sm"
            className="h-6 w-6 p-0"
            onClick={() => onOpenChange(false)}
            data-testid="button-close-login-modal"
          >
            <X className="h-4 w-4" />
          </Button>
        </DialogHeader>

        {authError && (
          <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded-md mb-4" data-testid="error-message">
            {authError}
          </div>
        )}

        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
            <FormTextField
              control={form.control}
              name="email"
              label="Email Address"
              type="email"
              placeholder="john@university.edu"
              testId="input-login-email"
            />

            <FormTextField
              control={form.control}
              name="password"
              label="Password"
              type="password"
              placeholder="â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢"
              testId="input-login-password"
            />

            <FormCheckboxField
              control={form.control}
              name="rememberMe"
              label="Remember me"
              testId="checkbox-remember-me"
            />

            <Button
              type="submit"
              className="w-full"
              disabled={isSubmitting}
              data-testid="button-login"
            >
              {isSubmitting ? "Signing In..." : "Sign In"}
            </Button>

            {/* Divider */}
            <div className="relative">
              <div className="absolute inset-0 flex items-center">
                <span className="w-full border-t" />
              </div>
              <div className="relative flex justify-center text-xs uppercase">
                <span className="bg-background px-2 text-muted-foreground">OR</span>
              </div>
            </div>

            {/* Social Login */}
            <div className="grid grid-cols-2 gap-4">
              <Button
                type="button"
                variant="outline"
                className="w-full"
                onClick={() => handleSocialLogin('google')}
                data-testid="button-google-login"
              >
                ðŸ”µ Google
              </Button>
              <Button
                type="button"
                variant="outline"
                className="w-full"
                onClick={() => handleSocialLogin('facebook')}
                data-testid="button-facebook-login"
              >
                ðŸ“˜ Facebook
              </Button>
            </div>

            <div className="text-center">
              <Button
                variant="link"
                className="text-sm"
                data-testid="button-forgot-password"
              >
                Forgot your password?
              </Button>
            </div>

            <div className="text-center text-sm">
              <span className="text-muted-foreground">Don't have an account? </span>
              <Button
                variant="link"
                className="p-0 h-auto text-sm"
                onClick={onSignupClick}
                data-testid="button-signup-link"
              >
                Sign Up
              </Button>
            </div>
          </form>
        </Form>
      </DialogContent>
    </Dialog>
  );
}

