import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import SignupModal from "@/components/auth/signup-modal";
import LoginModal from "@/components/auth/login-modal";
import EmailVerificationModal from "@/components/auth/email-verification-modal";

export default function AuthDemo() {
  const [showSignup, setShowSignup] = useState(false);
  const [showLogin, setShowLogin] = useState(false);
  const [showVerification, setShowVerification] = useState(false);
  const [verificationEmail, setVerificationEmail] = useState("");

  const handleSignupSuccess = (email: string) => {
    setVerificationEmail(email);
    setShowVerification(true);
  };

  const handleSwitchToLogin = () => {
    setShowSignup(false);
    setShowLogin(true);
  };

  const handleSwitchToSignup = () => {
    setShowLogin(false);
    setShowSignup(true);
  };

  const handleVerificationComplete = () => {
    console.log("Email verification completed!");
    // Here you would typically redirect to the dashboard or complete the onboarding
  };

  return (
    <div className="min-h-screen bg-background flex items-center justify-center p-4">
      <Card className="w-full max-w-md">
        <CardHeader className="text-center">
          <CardTitle className="text-2xl">Authentication Demo</CardTitle>
          <CardDescription>
            Test the sign-up, login, and email verification modals
          </CardDescription>
        </CardHeader>
        <CardContent className="space-y-4">
          <Button 
            className="w-full" 
            onClick={() => setShowSignup(true)}
            data-testid="button-demo-signup"
          >
            Open Sign Up Modal
          </Button>

          <Button 
            variant="outline" 
            className="w-full" 
            onClick={() => setShowLogin(true)}
            data-testid="button-demo-login"
          >
            Open Login Modal
          </Button>

          <Button 
            variant="outline" 
            className="w-full" 
            onClick={() => {
              setVerificationEmail("demo@university.edu");
              setShowVerification(true);
            }}
            data-testid="button-demo-verification"
          >
            Open Email Verification Modal
          </Button>
        </CardContent>
      </Card>

      {/* Auth Modals */}
      <SignupModal
        open={showSignup}
        onOpenChange={setShowSignup}
        onLoginClick={handleSwitchToLogin}
        onSignupSuccess={handleSignupSuccess}
      />

      <LoginModal
        open={showLogin}
        onOpenChange={setShowLogin}
        onSignupClick={handleSwitchToSignup}
      />

      <EmailVerificationModal
        open={showVerification}
        onOpenChange={setShowVerification}
        email={verificationEmail}
        onVerificationComplete={handleVerificationComplete}
      />
    </div>
  );
}

client/src/pages/vendors/list-service.ts.content:
import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import Navbar from "@/components/layout/navbar";
import Footer from "@/components/layout/footer";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Form, FormControl, FormDescription, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { insertVendorSchema } from "@shared/schema";
import { z } from "zod";
import { Store, Users, DollarSign, CheckCircle } from "lucide-react";

const formSchema = insertVendorSchema.extend({
  category: z.string().min(1, "Please select a category"),
  description: z.string().min(10, "Description must be at least 10 characters"),
});

type FormData = z.infer<typeof formSchema>;

export default function ListService() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [isSubmitting, setIsSubmitting] = useState(false);

  const form = useForm<FormData>({
    resolver: zodResolver(formSchema),
    defaultValues: {
      name: "",
      description: "",
      category: "",
      contactEmail: "",
      contactPhone: "",
      website: "",
      location: "",
      priceRange: "",
      isActive: true,
    },
  });

  const createVendorMutation = useMutation({
    mutationFn: (data: FormData) => apiRequest("/api/vendors", {
      method: "POST",
      body: JSON.stringify(data),
    }),
    onSuccess: () => {
      toast({
        title: "Service Listed Successfully!",
        description: "Your service has been submitted for review and will be live shortly.",
      });
      form.reset();
      queryClient.invalidateQueries({ queryKey: ["/api/vendors"] });
    },
    onError: (error: any) => {
      toast({
        variant: "destructive",
        title: "Error",
        description: error.message || "Failed to list your service. Please try again.",
      });
    },
  });

  const onSubmit = async (data: FormData) => {
    setIsSubmitting(true);
    try {
      await createVendorMutation.mutateAsync(data);
    } finally {
      setIsSubmitting(false);
    }
  };

  const benefits = [
    {
      icon: Users,
      title: "Reach Students",
      description: "Connect with thousands of students looking for your services"
    },
    {
      icon: DollarSign,
      title: "Grow Your Business",
      description: "Increase revenue by tapping into the student market"
    },
    {
      icon: CheckCircle,
      title: "Easy Management",
      description: "Simple dashboard to manage listings and track performance"
    }
  ];

  return (
    <div className="min-h-screen bg-background">
      <Navbar />

      <main className="pt-20">
        <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          {/* Header Section */}
          <div className="text-center mb-8">
            <h1 className="text-4xl font-bold text-foreground mb-4" data-testid="heading-list-service">
              List Your Service
            </h1>
            <p className="text-xl text-muted-foreground max-w-2xl mx-auto">
              Connect with students and grow your business by listing your services on our platform
            </p>
          </div>

          {/* Benefits Section */}
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
            {benefits.map((benefit, index) => (
              <Card key={benefit.title} className="text-center" data-testid={`card-benefit-${index}`}>
                <CardHeader>
                  <benefit.icon className="h-12 w-12 text-primary mx-auto mb-2" />
                  <CardTitle className="text-lg" data-testid={`text-benefit-title-${index}`}>
                    {benefit.title}
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <CardDescription data-testid={`text-benefit-description-${index}`}>
                    {benefit.description}
                  </CardDescription>
                </CardContent>
              </Card>
            ))}
          </div>

          {/* Service Listing Form */}
          <Card>
            <CardHeader>
              <CardTitle className="text-2xl flex items-center gap-2" data-testid="heading-service-form">
                <Store className="h-6 w-6" />
                Service Information
              </CardTitle>
              <CardDescription>
                Fill out the form below to list your service. All fields marked with * are required.
              </CardDescription>
            </CardHeader>
            <CardContent>
              <Form {...form}>
                <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <FormField
                      control={form.control}
                      name="name"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Service Name *</FormLabel>
                          <FormControl>
                            <Input placeholder="e.g., Academic Tutoring Services" {...field} data-testid="input-service-name" />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={form.control}
                      name="category"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Category *</FormLabel>
                          <Select onValueChange={field.onChange} defaultValue={field.value}>
                            <FormControl>
                              <SelectTrigger data-testid="select-category">
                                <SelectValue placeholder="Select a category" />
                              </SelectTrigger>
                            </FormControl>
                            <SelectContent>
                              <SelectItem value="tutoring">Tutoring</SelectItem>
                              <SelectItem value="textbooks">Textbooks</SelectItem>
                              <SelectItem value="housing">Housing</SelectItem>
                              <SelectItem value="food">Food & Catering</SelectItem>
                              <SelectItem value="technology">Technology</SelectItem>
                              <SelectItem value="transportation">Transportation</SelectItem>
                              <SelectItem value="other">Other</SelectItem>
                            </SelectContent>
                          </Select>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  </div>

                  <FormField
                    control={form.control}
                    name="description"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Description *</FormLabel>
                        <FormControl>
                          <Textarea 
                            placeholder="Describe your service, what makes it unique, and how it helps students..."
                            className="min-h-[100px]"
                            {...field}
                            data-testid="textarea-description"
                          />
                        </FormControl>
                        <FormDescription>
