interface ServicesListingProps {
  apiEndpoint?: string;
  title?: string;
  subtitle?: string;
  showCategories?: boolean;
  showFilters?: boolean;
  defaultCategory?: string;
  itemsPerPage?: number;
}

export default function ServicesListing({
  apiEndpoint = '/api/services',
  title = "Browse All Services",
  subtitle = "Discover 10,000+ verified services across India",
  showCategories = true,
  showFilters = true,
  defaultCategory = "",
  itemsPerPage = 20
}: ServicesListingProps) {
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedLocation, setSelectedLocation] = useState("");
  const [selectedCategory, setSelectedCategory] = useState(defaultCategory);
  const [selectedPriceRanges, setSelectedPriceRanges] = useState<string[]>([]);
  const [selectedRatings, setSelectedRatings] = useState<string[]>([]);
  const [selectedAvailability, setSelectedAvailability] = useState<string[]>([]);
  const [selectedSubjects, setSelectedSubjects] = useState<string[]>([]);
  const [selectedLevels, setSelectedLevels] = useState<string[]>([]);
  const [selectedUniversities, setSelectedUniversities] = useState<string[]>([]);
  const [selectedFeatures, setSelectedFeatures] = useState<string[]>([]);
  const [viewMode, setViewMode] = useState<'grid' | 'list'>('grid');
  const [currentPage, setCurrentPage] = useState(1);
  const [sortBy, setSortBy] = useState('popular');

  // Fetch services with filters
  const { data: servicesData, isLoading } = useQuery<{
    vendors?: any[];
    services?: any[];
    total: number;
  }>({
    queryKey: [apiEndpoint, {
      search: searchTerm,
      category: selectedCategory,
      limit: itemsPerPage,
      offset: (currentPage - 1) * itemsPerPage,
    }],
    enabled: true,
  });

  const handleSearch = (e: React.FormEvent) => {
    e.preventDefault();
    setCurrentPage(1);
  };

  const handleCategoryFilter = (category: string) => {
    setSelectedCategory(category === selectedCategory ? "" : category);
    setCurrentPage(1);
  };

  const toggleFilter = (value: string, currentFilters: string[], setFilters: (filters: string[]) => void) => {
    setFilters(
      currentFilters.includes(value) 
        ? currentFilters.filter(f => f !== value)
        : [...currentFilters, value]
    );
    setCurrentPage(1);
  };

  const clearFilters = () => {
    setSelectedCategory(defaultCategory);
    setSelectedPriceRanges([]);
    setSelectedRatings([]);
    setSelectedAvailability([]);
    setSelectedSubjects([]);
    setSelectedLevels([]);
    setSelectedUniversities([]);
    setSelectedFeatures([]);
    setSelectedLocation("");
    setSearchTerm("");
    setCurrentPage(1);
  };

  const hasActiveFilters = selectedCategory !== defaultCategory || selectedPriceRanges.length > 0 || 
    selectedRatings.length > 0 || selectedAvailability.length > 0 || selectedSubjects.length > 0 || 
    selectedLevels.length > 0 || selectedUniversities.length > 0 || selectedFeatures.length > 0 || 
    selectedLocation || searchTerm;

  // Compute safe data accessors
  const items = servicesData?.vendors ?? servicesData?.services ?? [];
  const total = servicesData?.total ?? items.length ?? 0;
  const totalPages = Math.ceil(total / itemsPerPage);

  return (
    <div className="min-h-screen bg-background">      
      {/* Header */}
      <div className="pt-8 pb-8 bg-muted/30">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center mb-8">
            <h1 className="text-4xl font-bold mb-2" data-testid="services-title">ðŸ” {title}</h1>
            <p className="text-muted-foreground text-lg" data-testid="services-subtitle">
              {subtitle}
            </p>
          </div>

          {/* Search & Filter Bar */}
          <Card className="mb-6">
            <CardContent className="p-6">
              <form onSubmit={handleSearch} className="flex flex-col lg:flex-row gap-4">
                <div className="relative flex-1">
                  <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground w-4 h-4" />
                  <Input
                    type="text"
                    placeholder="ðŸ” Search services, notes, hostels..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="pl-10"
                    data-testid="input-search-services"
                  />
                </div>

                <Select value={selectedLocation} onValueChange={(value) => setSelectedLocation(value === 'all' ? '' : value)}>
                  <SelectTrigger className="lg:w-48" data-testid="select-location">
                    <MapPin className="w-4 h-4 mr-2" />
                    <SelectValue placeholder="ðŸ“ Location" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All Locations</SelectItem>
                    {LOCATIONS.map(location => (
                      <SelectItem key={location.key} value={location.key}>
                        {location.label} ({location.count})
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>

                <Button type="submit" data-testid="button-search-services">
                  ðŸ” Search
                </Button>
              </form>

              <div className="mt-4 text-sm text-muted-foreground">
                ðŸ’¡ Try: "CS notes", "PG near IIT", "NEET coaching", "study groups"
              </div>
            </CardContent>
          </Card>

          {/* Results Count */}
          <div className="text-center text-muted-foreground">
            {servicesData?.total ? `${servicesData.total} services found` : 'Loading services...'}
          </div>
        </div>
      </div>

      {/* Category Tabs */}
      {showCategories && (
        <div className="py-6 border-b">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            <div className="flex items-center space-x-2 overflow-x-auto pb-2">
              {SERVICE_CATEGORIES.map((category) => (
                <Button
                  key={category.key}
                  variant={selectedCategory === category.key ? "default" : "outline"}
                  size="sm"
                  onClick={() => handleCategoryFilter(category.key)}
                  className="flex items-center space-x-2 whitespace-nowrap"
                  data-testid={`category-${category.key || 'all'}`}
                >
                  <span>{category.icon}</span>
                  <span>{category.label}</span>
                  <Badge variant="secondary" className="text-xs">
                    {category.count}
                  </Badge>
                </Button>
              ))}
            </div>
          </div>
        </div>
      )}

      {/* Main Content */}
      <div className="py-8">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex gap-8">
            {/* Sidebar Filters */}
            {showFilters && (
              <div className="hidden lg:block w-64 flex-shrink-0">
                <div className="space-y-6">
                  {/* Active Filters Summary */}
                  {hasActiveFilters && (
