            </div>
            <Button className="gap-2" data-testid="button-create-group">
              <Plus className="h-4 w-4" />
              Create Study Group
            </Button>
          </div>

          {/* Study Groups Grid or Map */}
          {viewMode === 'list' ? (
            <>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {filteredGroups.map((group) => (
