                </div>
              )}
            </>
          ) : (
            <div className="h-[600px]">
              <MapView 
                events={filteredGroups}
                onEventClick={(event) => console.log('Event clicked:', event)}
              />
            </div>
          )}
        </div>
      </main>

      <Footer />
    </div>
  );
}

